using namespace std;

typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else                                             
        return result;
}

int Q(int x, int y) {
	cout << "? " << x << ' ' << y << endl;
	fflush(stdout);
	int z;
	cin >> z;
	return z;
}


int main() {
    ll n, m, K;
    cin >> n >> m >> K;
	int L_x = 1, R_x = n;
	int L_y = 1, R_y = m;
	while (R_x - L_x > 1 || R_y - L_y > 1) {
		int mid_l = (L_x + R_x) >> 1;
		int mid_r = (L_y + R_y) >> 1;
		int X = Q(mid_l, mid_r);
		if (R_x - L_x > 1) {
			int x1 = Q(mid_l + 1, mid_r);
			if (X < x1) {
				R_x = mid_l;				
			} else {
				L_x = mid_l;				
			}
		}
		if (R_y - L_y > 1) {
			int x1 = Q(mid_l, mid_r + 1);
			if (X < x1) {
				R_y= mid_r;				
			} else {
				L_y = mid_r;			
			}
		}			
	}			
	cout << "! " << min(min(min(Q(L_x, L_y), Q(L_x, R_y)), Q(R_x, L_y)), Q(R_x, R_y));
    fflush(stdout);	
    return 0;
}