typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}

bool isprime(int n) {
	for (int i = 2; i * i <= n; i++) {
		if (n % i == 0) {
			return 0;
		}
	}
	return 1;
}

int binpow(int a, int n, int mod) {
	if (n == 0) {
		return 1 % mod;
	}
	if (n & 1) {
		return (a * binpow(a, n - 1, mod)) % mod;
	} else {
		int b = binpow(a, n / 2, mod) % mod;
		return (b*b)%mod;		
	}
}

