const long long MOD = 1e9 + 7;
const long long MAXN = 609;
using namespace std;

typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}
     
                                
int dp[MAXN][MAXN];


int L[MAXN];
int R[MAXN];

int go(int l, int r) {
	if (l > r) return 1;
	if (dp[l][r] != -1) {
		return dp[l][r];
	}
	for (int m = l; m <= r; m++) {
		int kol  = (m- l) * 2 + 1;
		if (go(l + 1, m) && go(m+1, r) && kol >= L[l] && kol <= R[l]) {
			return dp[l][r] = 1;
		}
	} 
	return dp[l][r] = 0;
}

void out(int l, int r) {
	for (int m = l; m <= r; m++) {
		int kol  = (m - l) * 2 + 1;
		if (dp[l+1][m] && dp[m+1][r] && kol >= L[l] && kol <= R[l]) {
			cout << '(';
			out(l+1, m);
			cout << ')';
			out(m+1, r);
			return;
		}
	}                 
}

int main() {
	#ifdef IZI_KATKA
	assert(freopen("input", "r", stdin));
    assert(freopen("output", "w", stdout));
    #endif
    int n = readInt();
	memset(dp, -1, sizeof (dp));
    for (int i = 1; i <= n; i++) {
    	L[i] = readInt(), R[i] = readInt();
    }
    go(1, n);
    if (!dp[1][n]) {
    	cout << "IMPOSSIBLE";
    	return 0;
    }
    out(1, n);
    return 0;
}