const long long MOD = 1e9 + 7;
const long long MAXN = 1e6 + 1;
using namespace std;

typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}

ll V[MAXN];
ll T[MAXN];

int pref[MAXN];

int main() {
	#ifdef IZI_KATKA
	assert(freopen("input", "r", stdin));
    assert(freopen("output", "w", stdout));
    #endif
    int n = readInt();
    for (int i = 1; i <= n; i++) {
    	V[i] = readInt();
    }
    for (int i = 1; i <= n; i++) {
    	T[i] = readInt();
    }
  	
	multiset <pair <ll, ll> > s;		
    ll sum = 0;
    long long cur = 0;
    for(int i = 1; i <= n; i++) {
        s.insert({V[i] + cur, cur});
        cur += T[i];
        ll ans = 0;
        while(s.size() > 0 && s.begin() -> F <= cur) {
            ll x = s.begin() -> F - (cur - T[i]);
            if(x >= 0) ans += x;
            else ans += s.begin() -> F - s.begin() -> S;
            s.erase(s.begin());
        }
        if(s.empty()) {
            cur = 0;
        }
        ans += s.size() * T[i];
        cout << ans << ' ';
    }
    return 0;
}