using namespace std;

typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}

const int N = 1111;

char a[N][N];
char b[N][N];


int d_x[] = {1, -1, 0, 0, +1, -1, +1, -1}; 
int d_y[] = {0, 0, -1, 1, -1, -1, +1, +1};

int main() {
	#ifdef IZI_KATKA
	assert(freopen("input", "r", stdin));
    assert(freopen("output", "w", stdout));
    #endif
    int n = readInt(), m = readInt();
    for (int i = 1; i <= n; i++) {
    	for (int j = 1; j <= m; j++) {
    		cin >> a[i][j];
        	b[i][j] = '.';
    	}
    }
    for (int i = 2; i <= n - 1; i++) {
    	for (int j = 2; j <= m - 1; j++) {
    		bool kek = 1;
    		for (int k = 0; k < 8; k++) {
    			kek &= (a[i+d_x[k]][j+d_y[k]] == '#');
			}
			if (kek) {
				for (int k = 0; k < 8; k++) {
    				b[i+d_x[k]][j+d_y[k]]='#';
    			}
			}
    	}
    }
    for (int i = 1; i <= n; i++) {
    	for (int j = 1; j <= m; j++) {
    		if (a[i][j] != b[i][j]) {
    			cout << "NO";
    			return 0;
    		}	
    	}
    }
    cout << "YES";
    return 0;
}