const long long MOD = 1e9 + 7;
const long long MAXN = 1e6 + 1;
using namespace std;

typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}

int pref[MAXN];

int S[MAXN];

int sum(int x) {
	return S[x];
}

main() {
	#ifdef IZI_KATKA
	assert(freopen("input", "r", stdin));
    assert(freopen("output", "w", stdout));
    #endif
    int n = readInt(), q = readInt();
    string s;
    cin >> s;
    s = '#' + s;
    S[0] = 1;
    for (int i = 1; i <= n; i++) {
		pref[i] = pref[i - 1] + (s[i] == '1');    	
    	S[i] = (S[i - 1] * 2) % MOD;
    }
    for (int i = 1; i <= q; i++) {    	
    	int l = readInt(), r = readInt();
		int cnt = pref[r] - pref[l - 1];
		int czt = (r - l + 1 - cnt);
		int sum1 = (sum(cnt) - 1 + MOD) % MOD;
		int sum2 = ((sum(czt) - 1 + MOD) % MOD * (sum1 % MOD))%MOD;
		cout << (sum1 + sum2)%MOD << endl;
    }
    return 0;
}