const long long MOD = 1e9 + 7;
const long long MAXN = 1e6 + 1;
using namespace std;

typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}

int a[MAXN];
vector <int> t[MAXN * 4];

void build(int v, int tl, int tr) {
	if (tl == tr) {
		t[v].pb(a[tl]);		
		return;
	} else {
		int tm = (tl + tr) / 2;
		build(v * 2, tl, tm);
		build(v * 2 + 1, tm + 1, tr);
		t[v].resize(t[v*2].size() + t[v*2+1].size());
		merge(t[v*2].begin(), t[v*2].end(), t[v*2+1].begin(), t[v*2+1].end(), t[v].begin());
	}
}

int Get(int v, int tl, int tr, int l, int r, int x) {
	if (r < tl || tr < l) return 0; 
	if (l <= tl && tr <= r) {
		int res = lower_bound(t[v].begin(), t[v].end(), x) - t[v].begin();
		return t[v].size() - res;
	} 
	int tm = (tl + tr) / 2;
	return Get(v * 2, tl, tm, l, r, x) + Get(v * 2 + 1, tm + 1, tr, l, r, x);
}

int main() {
	#ifdef IZI_KATKA
	assert(freopen("input", "r", stdin));
    assert(freopen("output", "w", stdout));
    #endif
    int n = readInt();
    for (int i = 1; i <= n; i++) {
    	a[i] = readInt();
    }
    build(1, 1, n);
    ll ans = 0;
    for (int i = 1; i <= n; i++) {
    	int res = Get(1, 1, n, 1, min(a[i], n), i);
    	if (a[i] >= i) res--;
    	ans += res;
    }
    cout << ans / 2;
    return 0;
}