#include <bits/stdc++.h>

#define F first
#define S second
#define endl '\n'
#define pb push_back

const long long MOD = 1e9 + 7;
const long long MAXN = 1e6 + 1;
using namespace std;

typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}

int a, b, n;

bool is_in(int x, int l, int r) {
	return (l <= x && x <= r);
}

void go(int lvl, int tl, int tr) {
	int mid = (tl + tr) / 2;
	if (is_in(a, tl, mid) && is_in(b, mid + 1, tr)) {
		if (lvl == 1) {
			cout << "Final!";
			exit(0);
		}
		cout << (log(n)/log(2))+1-lvl << endl;
		exit(0);
	} else {
		if (is_in(a, tl, mid) && is_in(b, tl, mid)) {
			go(lvl + 1, tl, mid);
		}		 else {
			go(lvl + 1, mid + 1, tr);
		}
	}                                  
}

int main() {
	#ifdef IZI_KATKA
	assert(freopen("input", "r", stdin));
    assert(freopen("output", "w", stdout));
    #endif
    n = readInt(), a = readInt(), b = readInt();
    if (a > b) swap(a, b);
    go(1, 1, n);
    return 0;
}