#include <bits/stdc++.h>

#define F first
#define S second
#define endl '\n'
#define pb push_back

const long long MOD = 1e9 + 7;
const long long MAXN = 1e6 + 1;
using namespace std;

typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}

int N, M, sz;
int a[MAXN];
int used[MAXN];
int timer;
int tmr = 1;

void dfs(int v) {

    used[v] = tmr;
    if (v - N >= 0 && used[v-N] != tmr)
    	dfs(v - N);
    if (v + M <= sz && used[v+M] != tmr)
    	dfs(v + M);
    a[v] = timer++;
} 

int main() {
	#ifdef IZI_KATKA
	assert(freopen("input", "r", stdin));
    assert(freopen("output", "w", stdout));
    #endif
    int T = readInt();
    while(T--) {
	    N = readInt(), M = readInt();
	    sz = N + M - __gcd(N, M) - 1;
	    timer = 0;
	    tmr ++;
	    cout << sz << endl;
		for (int i = 0; i <= sz; i++) {
			if (used[i] != tmr)
			dfs(i);
		}    
		for (int i = 0; i < sz; i++) {
			cout << a[i] - a[i+1] << ' ';
		}
		cout << endl;
	}
    return 0;
}