const long long MOD = 1e9 + 7;
const long long MAXN = 1e6 + 1;
using namespace std;

typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}


double dist(pair <double, double> a, pair <double, double> b) {
	int X = a.F - b.F;
	int Y = a.S - b.S;
	return sqrt(X * X + Y* Y);
}

pair <double, double> get_point(pair <double, double> a, pair <double, double> b) {
	return make_pair((a.F + b.F) / 2.0, (a.S + b.S) / 2.0);
}

pair <double, double> a[MAXN];
int main() {
	#ifdef IZI_KATKA
	assert(freopen("input", "r", stdin));
    assert(freopen("output", "w", stdout));
    #endif
    int t = readInt();
    while(t--) {
    	int n = readInt();
		for (int i = 1; i <= n; i++) {
			a[i] = {readInt(), readInt()};
		}
		double mn_dist = MOD;
		pair <double, double> p;
		for (int i = 1; i <= n; i++) {
		    for (int j = 1; j < i; j++) {
		    	if (mn_dist > dist(a[i], a[j])) {
		    		mn_dist = dist(a[i], a[j]);
		    		p = a[i];
		    	}	               
		    }
		}
    	cout << fixed << setprecision(3) << p.F << ' ' << p.S << endl << endl;;
    }
    return 0;
}