#include <bits/stdc++.h>

#define F first
#define S second
#define endl '\n'
#define pb push_back

const long long MOD = 1e9 + 7;
const long long MAXN = 1e6 + 1;
using namespace std;

typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}

int timer = 1;

int used[MAXN];

vector <int> g[MAXN];

int mx_num = 0;
int mx = 0;

struct node {
	map <int, bool> maps;
	vector <int> vec;
}; 
vector <int> ans;

void dfs(int v, int depth, node tmp) {
	if (mx < depth) {
		depth = mx;
		ans = tmp.vec;
	}
	tmp.maps[v] = 1;	
	for (int i : g[v]) {
		if (!tmp.maps.count(i)) {
			tmp.vec.pb(i);
			dfs(i, depth + 1, tmp);			
			tmp.vec.pop_back();
		}
	}
}

int main() {
	#ifdef IZI_KATKA
	assert(freopen("input", "r", stdin));
    assert(freopen("output", "w", stdout));
    #endif
    int n = readInt();
    for (int i = 1; i <= n; i++) {
    	for (int j = 1; j <= n; j++) {
    		int x = readInt();
    		if (x) {
    			g[i].pb(j);
    		}
    	}
    }
    for (int i = 1; i <= n; i++) {
		mx = 0;
		mx_num = 0;
		node tmp;
		ans.clear();
		dfs(i, 0, tmp);
		cout << ans.size()+1 << ' ';
		cout << i << ' ';
		for (int j : ans) {
			cout << j << ' ';
		}
		cout << endl;		
    }
    return 0;
}