using namespace std;

typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}


 main() {
	ll N = 100000000;
	
	int lp[N+1];
	vector<int> pr;
 
	for (int i=2; i<=N; ++i) {
		if (lp[i] == 0) {
			lp[i] = i;
			pr.push_back (i);
		}
		for (int j=0; j<(int)pr.size() && pr[j]<=lp[i] && i*pr[j]<=N; ++j)
			lp[i * pr[j]] = pr[j];
	}
	int T = readInt();
	while(T--) {
		int x = readInt();
		if (lp[x] == x) {
			int k = 0;
			int i = 1;
			while(pr[k+i] <= x) {
				k += (i);
				i++;
			}
			while(pr[k] != x) k++;
			cout << k << endl;	
		} else {
			cout << -1 << endl;
		}
	}
    return 0;
}