const long long MOD = 1e9 + 7;
const long long MAXN = 1e6 + 1;
using namespace std;

typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}

ll a[MAXN];

ll n, k;

ll ct(double x) {
	ll tot = 0;
	for (int i = 1; i <= n; ++i)
        tot += ll((sqrt(1 + 4*a[i]/x)-1)/2);
	return tot;
}

int main() {

	assert(freopen("tallbarn.in", "r", stdin));
    assert(freopen("tallbarn.out", "w", stdout));
	n = readInt(), k = readInt();
    k -= n;
    for (int i = 1; i <= n; i++) {
    	a[i] = readInt();
    }
	double lo = 0, hi = 1e18;
	for (int i = 0; i < 200; ++i) {
		double mid = (lo+hi)/2;
		if (ct(mid) >= k)
			lo = mid;
		else
			hi = mid;
	}
	ll tot = 0;
	double ans = 0;
	for (int i = 1; i <= n; ++i) {
		ll x = ll((sqrt(1 + 4*a[i]/lo)-1)/2);
		ans += 1.0*a[i]/(x+1);
		tot += x;
	}
    
	cout << (ll)round(ans - (k-tot)*lo) << endl;
	return 0;
}                                                             