typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}

int v[2][MAXN];
int t[2][MAXN];

void upd(int arr, int v, int tl, int tr, int pos, int val) {
	if (tl == tr) {
		t[arr][v] = val;
		return;
	}
	int tm = (tl + tr) / 2;
	if (pos <= tm) {
		upd(arr, v * 2, tl, tm, pos, val);
	} else {
		upd(arr, v * 2 + 1, tm + 1, tr, pos, val);
	}
	t[arr][v] = t[arr][v * 2] + t[arr][v * 2 + 1]; 
}


