const long long MOD = 1e18;
const long long MAXN = 1e6 + 1;
using namespace std;

typedef long long ll;

long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}


ll f(ll x) {
    ll razryad = 1;
    while (razryad * 10 <= x) razryad *= 10;
    ll cnt = 0;
    while (x > 0) {
        ll viswiy = x / razryad;
        ll kek = (x % (razryad)) / viswiy + 1;
        cnt += kek;
        x -= (kek * viswiy);
        if (razryad > x) razryad /= 10;
    }
    return cnt + 1;
}

int main() {
	#ifdef IZI_KATKA
	assert(freopen("input", "r", stdin));
    assert(freopen("output", "w", stdout));
    #endif
    ll n = readInt();
    ll l = 0, r = MOD;
    while(r - l > 1) {
    	ll mid = (l + r) / 2;
    	if (f(mid) <= n) {
    		l = mid;
    	} else {
			r = mid;    		
    	}
    }
    if (f(r) == n) {
    	cout << r;
    } else if (f(l) == n){
    	cout << l;
    } else {
		assert(0);
    }
    return 0;     
}