const long long MOD = 1e9 + 7;
const long long MAXN = 1e6 + 1;
const long long INF = 1e15;
using namespace std;

typedef long long ll;
typedef pair<ll, ll> pii;


long long readInt() {
    bool minus1 = false;
    long long result = 0;
    char ch;
    ch = getchar();
    while (true) {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus1 = true; else result = ch-'0';
    while (true) {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus1)
        return -result;
    else
        return result;
}

const int N = 1111;
ll dp[N][N];

pii a[N];

bool comp(pii x, pii y) {
	pii X = {x.S, x.F};
	pii Y = {y.S, y.F};
	return X < Y;	
}

int main() {
	#ifdef IZI_KATKA
	assert(freopen("input", "r", stdin));
    assert(freopen("output", "w", stdout));
    #endif
    int n = readInt();
    for (int i = 1; i <= n; i++) {
    	a[i].F = readInt();
    }
    for (int j = 1; j <= n; j++) {
    	a[j].S = readInt();
    }
    sort(a + 1, a + 1 + n, comp);
	for (int i = 0; i <= n; i++) {
		for (int j = 0; j <= n; j++) {
			dp[i][j] = INF;
		}
	}
	dp[0][0] = 0;
    for (int i = 1; i <= n; i++) {
    	for (int j = 1; j <= i; j++) {
    		dp[i][j] = dp[i - 1][j];
    		if (dp[i - 1][j - 1] <= a[i].S -  a[i].F) {
    			dp[i][j] = min(dp[i][j], dp[i - 1][j - 1] + a[i].F);
    		}
    	}
    }
    for (int i = n; i >= 0; i--) {
    	if (dp[n][i] != INF) {
    		cout << i;
    		return 0;
    	}
    }
    cout << 0;
    return 0;
}